
import BN from 'bn.js';

import { Cell } from '../boc/cell/cell';
import { HttpProvider } from '../http-provider/http-provider';
import { Address } from '../utils/address';
import { Contract, ContractMethods, ContractOptions, Method } from './contract';


export interface SubscriptionContractOptions extends ContractOptions {
    wallet?: Address;
    beneficiary?: Address;
    amount?: BN;
    period?: number;
    timeout?: number;
    startAt?: number;
    subscriptionId?: number;
}

export interface SubscriptionContractMethods extends ContractMethods {
    pay: () => Method;
    getSubscriptionData: () => Promise<SubscriptionData>;
}

export interface PayExternalMessage {
    address: Address;
    message: Cell;
    body: Cell;
    signature?: Uint8Array;
    cell?: Cell;
    resultMessage?: Cell;
}

export interface SubscriptionData {
    wallet: string;
    beneficiary: string;
    amount: BN;
    period: number;
    startAt: number;
    timeout: number;
    lastPayment: number;
    lastRequest: number;
    failedAttempts: number;
    subscriptionId: number;
}


export class SubscriptionContract extends Contract<
    SubscriptionContractOptions,
    SubscriptionContractMethods
> {

    constructor(
        provider: HttpProvider,
        options: SubscriptionContractOptions
    ) {
        options.code = Cell.oneFromBoc(
            ''
        );
        super(provider, options);

        this.methods.pay = () => Contract.createMethod(
            provider,
            this.createPayExternalMessage()
        );

        this.methods.getSubscriptionData = (
            () => this.getSubscriptionData()
        );

    }


    /**
     * Creates payment body (from wallet to subscription).
     */
    public createBody(): Cell {
        const body = new Cell();
        const op = new BN(0x706c7567).add(new BN(0x80000000));
        body.bits.writeUint(op, 32);
        return body;
    }

    /**
     * Destroys plugin body (from wallet to subscription OR
     * from beneficiary to subscription).
     */
    public createSelfDestructBody(): Cell {
        const body = new Cell();
        body.bits.writeUint(0x64737472, 32); // op
        return body;
    }

    public async getSubscriptionData(): Promise<SubscriptionData> {

        const myAddress = await this.getAddress();

        const result = await this.provider.call2(
            myAddress.toString(),
            'get_subscription_data'
        );

        const wallet = parseAddress(result[0]);
        const beneficiary = parseAddress(result[1]);
        const amount = result[2];
        const period = result[3].toNumber();
        const startAt = result[4].toNumber(); // start_time
        const timeout = result[5].toNumber();
        const lastPayment = result[6].toNumber(); // last_payment_time
        const lastRequest = result[7].toNumber(); // last_request_time
        const failedAttempts = result[8].toNumber();
        const subscriptionId = result[9].toNumber();

        return {
            wallet,
            beneficiary,
            amount,
            period,
            startAt,
            timeout,
            lastPayment,
            lastRequest,
            failedAttempts,
            subscriptionId,
        };


        function parseAddress(tuple): string {
            return (
                tuple[0].toNumber() + ':' +
                tuple[1].toString(16)
            );
        }

    }


    protected createDataCell() {
        const cell = new Cell();
        cell.bits.writeAddress(this.options.wallet);
        cell.bits.writeAddress(this.options.beneficiary);
        cell.bits.writeGrams(this.options.amount);
        cell.bits.writeUint(this.options.period, 32);
        cell.bits.writeUint(this.options.startAt, 32); // start_time
        cell.bits.writeUint(this.options.timeout, 32);
        cell.bits.writeUint(0, 32); // last_payment_time
        cell.bits.writeUint(0, 32); // last_request_time
        cell.bits.writeUint(0, 8); // failed_attempts
        cell.bits.writeUint(this.options.subscriptionId, 32); // subscription_id
        return cell;
    }

    protected async createPayExternalMessage(): Promise<PayExternalMessage> {

        const selfAddress = await this.getAddress();
        const header = Contract.createExternalMessageHeader(selfAddress);
        const resultMessage = Contract.createCommonMsgInfo(header, null, null);
        const body = new Cell();

        // This is not required by the contract;
        // just to make it easier to distinguish messages
        body.bits.writeUint(Math.floor(Date.now() / 1000), 64);

        return {
            address: selfAddress,
            message: resultMessage,
            body: body
        };

    }

}
