
import BN from 'bn.js';

import { Cell } from '../../../boc/cell/cell';
import { HttpProvider } from '../../../http-provider/http-provider';
import { Address } from '../../../utils/address';
import { Contract, ContractMethods, ContractOptions } from '../../contract';
import { parseAddress } from './utils';


export interface NftSaleOptions extends ContractOptions {
    marketplaceAddress?: Address;
    nftAddress?: Address;
    fullPrice?: BN;
    marketplaceFee?: BN;
    royaltyAddress?: Address;
    royaltyAmount?: BN;
}

export interface NftSaleMethods extends ContractMethods {
    getData: () => Promise<NftSaleData>;
}

// @todo type these anys
export interface NftSaleData {
    marketplaceAddress?: Address;
    nftAddress?: Address;
    nftOwnerAddress?: Address;
    fullPrice: any;
    marketplaceFee: any;
    royaltyAddress?: Address;
    royaltyAmount: any;
}

export interface CreateCancelBodyParams {
    queryId?: number;
}


const NFT_SALE_HEX_CODE = (
    ''
);


/**
 * Work in progress, will be changed.
 */
export class NftSale extends Contract<NftSaleOptions, NftSaleMethods> {

    public static codeHex = NFT_SALE_HEX_CODE;


    constructor(provider: HttpProvider, options: NftSaleOptions) {
        options.wc = 0;
        options.code = (options.code || Cell.oneFromBoc(NFT_SALE_HEX_CODE));
        super(provider, options);

        this.methods.getData = () => this.getData();
    }


    public async getData(): Promise<NftSaleData> {

        const myAddress = await this.getAddress();
        const result = await this.provider.call2(
            myAddress.toString(),
            'get_sale_data'
        );

        const marketplaceAddress = parseAddress(result[0]);
        const nftAddress = parseAddress(result[1]);
        const nftOwnerAddress = parseAddress(result[2]);
        const fullPrice = result[3];
        const marketplaceFee = result[4];
        const royaltyAddress = parseAddress(result[5]);
        const royaltyAmount = result[6];

        return {
            marketplaceAddress,
            nftAddress,
            nftOwnerAddress,
            fullPrice,
            marketplaceFee,
            royaltyAddress,
            royaltyAmount,
        };

    }

    public async createCancelBody(
        params: CreateCancelBodyParams

    ): Promise<Cell> {

        const cell = new Cell();
        cell.bits.writeUint(3, 32); // cancel op
        cell.bits.writeUint(params.queryId || 0, 64);
        return cell;
    }


    /**
     * Returns cell that contains NFT sale data.
     */
    protected createDataCell(): Cell {
        const cell = new Cell();
        cell.bits.writeAddress(this.options.marketplaceAddress);
        cell.bits.writeAddress(this.options.nftAddress);
        cell.bits.writeAddress(null); // nft_owner_address
        cell.bits.writeCoins(this.options.fullPrice);

        const feesCell = new Cell();
        feesCell.bits.writeCoins(this.options.marketplaceFee);
        feesCell.bits.writeAddress(this.options.royaltyAddress);
        feesCell.bits.writeCoins(this.options.royaltyAmount);
        cell.refs[0] = feesCell;

        return cell;
    }

}
